	<?php 		global $current_user;	$user_id = $current_user->ID;	// Determin Course Access 	$umeta_key = 'course_access';	$usingle = true;	$ca_num = intval( get_user_meta( $user_id, $umeta_key, $usingle ) );	$acc_arr = array(		1 => "main course only",		2 => "main and childbirth courses",		3 => "all course materials"	);	if( ( $ca_num >= 1 ) && ( $ca_num <= 3 ) ){		$status_message .= "You currently have access to <strong>".$acc_arr[$ca_num]."</strong>.";	}	$manuals_arr = array(		'main' => 'Main Course',		'childbirth' => 'Childbirth Course',		'doula-actions' => 'Doula Actions',		'case-studies' => 'Case Studies',		'birth packet' => 'Birth Packet'	);		$stud_mat_arr = array();	switch( $ca_num ){		case 3:			$stud_mat_arr[] = 'doula-actions';					case 2:			array_unshift( $stud_mat_arr, 'childbirth' );					case 1:			array_push( $stud_mat_arr, 'case-studies', 'birth-packet');			array_unshift( $stud_mat_arr,  'main' );			break;					case 0:		default:			$status_message .= "You do not have access to these materials.";			break;	}						// Determine Account Active Status	$s_active = current_user_can('student_current');	$site_url = home_url();		$nb_code = '';	if( !$s_active ){ 		$nb_code .= "<p>Hmm, Looks like your account is marked as <em>inactive</em>. <a href='$site_url/inactive-student-notice'>Get details.</a></p>";	} else {		$nb_code .=  "<p>Current course manuals are now available online in web format. First-time students, START with <a href='$site_url/manuals/birth-doula/main/' >the MAIN COURSE manual</a>.  Our archieved <a href='$site_url/pdf-manuals/' target='_blank' >PDF Manuals are still avaialble</a> for reference's sake, but are now obsolete.</p>				<ul id='section-list'>			<li class='ct_cert page_item'>						<a href='/manuals/birth-doula/'>				<span class='ct_name'>CERTIFICATION</span>				<span class='course_title'>Birth Doula</span>			</a>			</li>		</ul>  		";	}		?>			<div <?php post_class(); ?> > 		